mapper.select:
SELECT order_id, product_id, buyer_id, status, amount, toss_order_id, created_at, updated_at
FROM orders
WHERE order_id = #{orderId}

SELECT order_id, product_id, buyer_id, status, amount, created_at, toss_order_id, updated_at
FROM orders
WHERE toss_order_id = #{tossOrderId}

mapper.update:
UPDATE orders
SET status = 'PAYMENT_PENDING', buyer_id = #{buyerId}, amount = #{amount}, updated_at = NOW()
WHERE order_id = #{orderId}

mapper.select:
SELECT product_id
FROM chat_rooms
WHERE room_id = #{roomId}

SELECT o.order_id, o.product_id, o.buyer_id, o.status, o.amount, o.toss_order_id, o.created_at, o.updated_at, p.seller_id, p.title as product_title, d.status as delivery_status, u.nickname as buyer_nickname, u.name as buyer_name FROM orders o INNER JOIN products p ON o.product_id = p.product_id INNER JOIN users u ON o.buyer_id = u.user_id LEFT JOIN deliveries d ON o.order_id = d.order_id WHERE p.seller_id = #{sellerId}
AND o.status = #{status}
ORDER BY o.created_at DESC
