mapper.select:
SELECT order_id, product_id, buyer_id, status, amount, toss_order_id, created_at, updated_at
FROM orders
WHERE order_id = #{orderId}

SELECT order_id, product_id, buyer_id, status, amount, created_at, toss_order_id, updated_at
FROM orders
WHERE toss_order_id = #{tossOrderId}

mapper.update:
UPDATE orders
SET status = 'PAYMENT_PENDING', buyer_id = #{buyerId}, amount = #{amount}, updated_at = NOW()
WHERE order_id = #{orderId}
